import React, { Component } from "react";
import { Text, View, SafeAreaView, StyleSheet } from "react-native";
import * as Location from "expo-location";
import Weather from "../../../components/Weather";
import { API_KEY } from "../../../utils/WeatherAPIKey";
import Icon from "react-native-vector-icons/MaterialIcons";

class Home extends Component {
  state = {
    isLoading: false,
    temperature: 0,
    weatherCondition: null,
    error: null,
  };

  componentDidMount() {
    Location.requestForegroundPermissionsAsync()
      .then(({ status }) => {
        if (status === "granted") {
          Location.getCurrentPositionAsync()
            .then((position) => {
              this.fetchWeather(
                position.coords.latitude,
                position.coords.longitude
              );
            })
            .catch((error) => {
              console.error("Error Getting Weather Conditions:", error);
              this.setState({
                isLoading: false,
                error: "Error Getting Weather Conditions",
              });
            });
        } else {
          this.setState({
            isLoading: false,
            error: "Location permission not granted",
          });
        }
      })
      .catch((error) => {
        console.error("Error Getting Location Permission:", error);
        this.setState({
          isLoading: false,
          error: "Error Getting Location Permission",
        });
      });
  }

  fetchWeather = (lat = 25, lon = 25) => {
    this.setState({ isLoading: true });

    fetch(
      `http://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}&units=metric`
    )
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        this.setState({
          isLoading: false,
          temperature: json.main.temp,
          weatherCondition: json.weather[0].main,
        });
      })
      .catch((error) => {
        console.error(error);
        this.setState({ isLoading: false, error: "Error Fetching Weather" });
      });
  };

  render() {
    const { isLoading, temperature, error } = this.state;

    return (
      <SafeAreaView style={{ flex: 1, backgroundColor: "white", padding: 10 }}>
        <View
          style={{
            marginHorizontal: 10,
            backgroundColor: "#C5CDC6",
            flex: 1,
          }}
        >
          <View style={styles.weatherContainer}>
            <View style={styles.top}>
              {isLoading ? (
                <Text style={styles.loadingText}>Fetching The Weather</Text>
              ) : error ? (
                <Text style={styles.errorText}>{error}</Text>
              ) : (
                <Weather temperature={temperature} />
              )}
            </View>
            <View
              style={{
                marginTop: 50,
                flexDirection: "row",
                marginLeft: 20,
                backgroundColor: "gray",
                justifyContent: "center",
                alignItems: "center",
                width: 150,
                borderRadius: 10,
                height: 100,
              }}
            ></View>
          </View>
        </View>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  top: {
    marginTop: 50,
    flexDirection: "row",
    marginLeft: 20,
    backgroundColor: "gray",
    justifyContent: "center",
    alignItems: "center",
    width: 150,
    borderRadius: 10,
    height: 100,
  },
  loadingText: {
    fontSize: 16,
    color: "rgba(236, 153, 95, 1)",
  },
  errorText: {
    fontSize: 16,
    color: "red",
  },
  weatherContainer: {
    justifyContent: "flex-start",
  },
});

export default Home;
